// Garage: Look up main vehicle for this member
$garage_veh_request = $smcFunc['db_query']('', '
    SELECT CONCAT_WS(\' \', v.made_year, mk.make, md.model) AS vehicle, v.id AS vid
    FROM {db_prefix}garage_vehicles AS v
    INNER JOIN {db_prefix}garage_makes AS mk ON v.make_id = mk.id
    INNER JOIN {db_prefix}garage_models AS md ON v.model_id = md.id
    WHERE v.user_id = {int:user_id}
        AND v.main_vehicle = 1
        AND v.pending != {string:pending}
        AND mk.pending != {string:pending}
        AND md.pending != {string:pending}
    LIMIT 1',
    array(
        'user_id' => $message['id_member'],
        'pending' => '1',
    )
);
if ($garage_veh_row = $smcFunc['db_fetch_assoc']($garage_veh_request)) {
    $memberContext[$message['id_member']]['vid'] = $garage_veh_row['vid'];
    $memberContext[$message['id_member']]['vehicle'] = $garage_veh_row['vehicle'];
} else {
    $memberContext[$message['id_member']]['vid'] = '';
    $memberContext[$message['id_member']]['vehicle'] = '';
}
$smcFunc['db_free_result']($garage_veh_request);

